import "../App.css";
import React from "react";

function Login(){
  
  function checkLogin(){
    const userN = document.getElementById("un").value;
    const userP = document.getElementById("pwd").value;
  
    console.log("Név: "+userN);
    fetch(`http://localhost:5000/api/user/letolt/${userN}/${userP}`,
      { method: "POST",
        headers: {
        Accept: 'application/json',
        "Content-Type": 'application/json'
        },
      }).then(res =>
      {
        if(res.status ===200) {
          this.props.loggedIn();
      } else if(res.status ===401){
          console.log(res.status);
          alert('wrong username or password');       
      }else{
          const error = new Error(res.error);
          throw error;
      }
      });
  }
    
    return(
        <form>
            <input type="text" id="un" placeholder="felhasználónév..." name="nev"/>
            <input type="password" id="pwd" placeholder="jelszó..." name="password"/>
            <button onClick={(e) => checkLogin()} >Login</button>
            
        </form>
    );
}
export default Login;
